SELECT student.under_grad_course,student.bachelors_degree,student.id_number,student.date_enrolled,student.student_name,student.year_level, program.program_name,  subject.subject_code, subject.subject_description, subject.unit_load, enrollments.yr_sem_string FROM student INNER JOIN program on student.program_id = program.id INNER JOIN enrollments on student.id = enrollments.student_id INNER JOIN year_sem on enrollments.yrsem_id = year_sem.id INNER JOIN enrollments_subject on enrollments.id = enrollments_subject.enrollments_subject_id INNER JOIN subject on enrollments_subject.subject_id = subject.id where student.id = '100' order by enrollments.id